function noMoreAjax(item) {
 var linkItem = $(item);
 linkItem.removeClass("ajax-link");
 linkItem.addClass("link-prevent");
 linkItem.unbind("click");
 $(linkItem).click(function(event) {
  event.preventDefault();
 });
}
