// OWL CAROUSEL

$(document).ready(function () {
    $('.owl-carousel').owlCarousel({
        loop: true,
        margin: 0,
        nav: true,
        // center: true,
        items: 1,
        lazyLoad: true,
        responsive: {
            480: {
                stagePadding: 60
            },
            576: {
                stagePadding: 100
            },
            768: {
                stagePadding: 150
            },
            992: {
                stagePadding: 200
            },
            1200: {
                stagePadding: 230
            },
            1800: {
                stagePadding: 600
            },
        }
    });
}); // Any changes to these values need to be copied to owl.theme.default.min.css too